package nl.jiankai.year2022;

import java.util.HashSet;
import java.util.Set;

public class Day6 {

    public static void main(String[] args) {
        part1();
        part2();
    }

    private static void part1() {
        findAnswer(4);
    }

    private static void part2() {
        findAnswer(14);
    }

    public static void findAnswer(int consecutive) {
        String input = "\n";
        Set<Character> cs = new HashSet<>();

        for (int i = consecutive-1; i < input.length(); i++) {
            for (int j = i; j >= i - consecutive+1; j--) {
                cs.add(input.charAt(j));
            }

            if (cs.size() == consecutive) {
                System.out.println(i + 1);
                break;
            } else {
                cs.clear();
            }
        }
    }
}
